/**
 * File customizer.js.
 *
 * Theme Customizer enhancements for a better user experience.
 *
 * Contains handlers to make Theme Customizer preview reload changes asynchronously.
 */
!function(m){
// Default color.
wp.customize("colorscheme",function(e){e.bind(function(e){
// Update custom color CSS.
var t=m("#custom-theme-colors"),o=t.data("hue"),i=t.html(),c;
//If a "custom" color option is selected, use the currently set colorscheme_primary_hue
c="custom"===e?wp.customize.get().colorscheme_primary_hue:199,
// Equivalent to css.replaceAll, with hue followed by comma to prevent values with units from being changed.
i=i.split(o+",").join(c+","),t.html(i).data("hue",c)})}),
// Primary color.
wp.customize("colorscheme_primary_hue",function(e){e.bind(function(e){
// Update custom color CSS.
var t=m("#custom-theme-colors"),o=t.data("hue"),i=t.html();
// Equivalent to css.replaceAll, with hue followed by comma to prevent values with units from being changed.
i=i.split(o+",").join(e+","),t.html(i).data("hue",e)})}),
// Image filter.
wp.customize("image_filter",function(e){e.bind(function(e){"active"===e?m("body").addClass("image-filters-enabled"):m("body").removeClass("image-filters-enabled")})}),
// update phone number
wp.customize("nus_phone"),
// update email
wp.customize("nus_email")}(jQuery);