/**
 * File customizer.js.
 *
 * Theme Customizer enhancements for a better user experience.
 *
 * Contains handlers to make Theme Customizer preview reload changes asynchronously.
 */
!function(n){
// update phone number
wp.customize("nus_phone",function(t){t.bind(function(t){n(".visitor-tools .contact--phone").html('<a href="tel://'+t+'">'+t+"</a>")})}),
// update email
wp.customize("nus_email",function(t){t.bind(function(t){n(".visitor-tools .contact--email").html('<a href="mailto:'+t+'" title="Email Sanford Harmony">'+t+"</a>")})})}(jQuery);